import React, { useState, useEffect } from 'react';
import { useUnreadCount } from '../UnreadCountContext';

const Notifications = () => {
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      user: 'John Smith',
      message: 'Selling a desk lamp',
      date: '2024-09-15T10:30:00Z',
      isRead: false,
    },
    {
      id: 2,
      user: 'Emily White',
      message: 'Looking for a mini fridge',
      date: '2024-09-13T09:15:00Z',
      isRead: true,
    },
    // Add more notifications as needed
  ]);

  const { setUnreadCount } = useUnreadCount();

  useEffect(() => {
    // Sort notifications by date in descending order
    const sortedNotifications = [...notifications].sort(
      (a, b) => new Date(b.date) - new Date(a.date)
    );
    setNotifications(sortedNotifications);

    // Update unread count
    const count = sortedNotifications.filter(notification => !notification.isRead).length;
    setUnreadCount(count);

    // Store unread count in session storage
    sessionStorage.setItem('notificationCount', count);
  }, [notifications, setUnreadCount]);

  return (
    <div style={styles.container}>
      <div style={styles.notifications}>
        {notifications.map((notification) => (
          <div key={notification.id} style={styles.notification}>
            <div style={styles.header}>
              <div style={styles.user}>{notification.user}</div>
              <div style={styles.date}>
                {new Date(notification.date).toLocaleString()}
              </div>
            </div>
            <div style={notification.isRead ? styles.boldMessage : styles.message}>
              {notification.message}
            </div>
            <button style={styles.contactButton}>Contact</button>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    maxWidth: '100vw',
    backgroundColor: '#f5f8fa',
    fontFamily: 'Arial, sans-serif',
  },
  notifications: {
    display: 'grid',
    gap: '1rem',
  },
  notification: {
    padding: '1rem',
    backgroundColor: '#fff',
    border: '1px solid #e1e8ed',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.5rem',
  },
  user: {
    fontWeight: 'bold',
  },
  date: {
    color: '#657786',
  },
  message: {
    marginBottom: '1rem',
    color: '#333',
  },
  boldMessage: {
    marginBottom: '1rem',
    color: '#333',
    fontWeight: 'bold',
  },
  contactButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#1da1f2',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1rem',
  },
};

export default Notifications;
