  "Shoes": ["sneakers", "boots", "sandals", "heels", "flats", "loafers", "running shoes", "dress shoes", "flip flops", "slippers", "wedges", "oxfords", "ballet flats", "espadrilles", "clogs", "ankle boots", "platforms", "Nike", "Adidas", "Puma", "Reebok", "Converse", "Vans", "Timberlands", "Dr. Martens", "Clarks", "New Balance", "Under Armour", "Skechers", "Crocs", "Birkenstock", "UGG", "Asics", "Jordan", "Gucci", "Louis Vuitton", "Prada", "Balenciaga"],
  "Crafts & Hobbies": ["sewing machine", "fabric", "paintbrushes", "paint", "canvas", "yarn", "knitting needles", "scissors", "glue", "beads", "scrapbooking paper", "crochet hooks"],
};

const Home = ({ bookmarks, toggleBookmark, searchTerm }) => {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [items, setItems] = useState([]);

  useEffect(() => {
    const storedItems = sessionStorage.getItem('items');
    if (storedItems) {
      setItems(JSON.parse(storedItems));
    } else {
      const fetchItems = async () => {
        try {
          const response = await axios.get('http://127.0.0.1:8000/api/v1/items/');
          setItems(response.data);
          sessionStorage.setItem('items', JSON.stringify(response.data));
        } catch (error) {
          console.error('Error fetching items:', error.response ? error.response.data : error.message);
        }
      };
      fetchItems();
    }
  }, []);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  // Filter items based on category and search term
  const filteredItems = items.filter(item => {
    const categoryMatch = selectedCategory === 'All' || item.category === selectedCategory;
    const searchMatch = searchTerm === '' || item.name.toLowerCase().includes(searchTerm);
    return categoryMatch && searchMatch;
  });

  return (
    <div style={{ padding: '2rem', maxWidth: '100vw' }}>
      <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        {CATEGORIES.map((category) => (
          <button
            key={category}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: selectedCategory === category ? '#B3A369' : '#fff',
              color: selectedCategory === category ? '#fff' : '#B3A369',
              border: '1px solid #B3A369',
              cursor: 'pointer',
              borderRadius: '4px',
            }}
            onClick={() => handleCategoryChange(category)}
          >
            {category}
          </button>
        ))}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '1rem' }}>
        {filteredItems.map((item) => (
          <ItemComponent
            key={item._id}
            item={item}
            bookmarks={bookmarks}
            toggleBookmark={toggleBookmark}
            style={{ height: '310px', width: '320px', overflow: 'hidden' }} // Set consistent height
          />
        ))}
      </div>
    </div>
  );
};

export default Home;
