import React, { useState, useEffect } from 'react';
import { useUnreadCount } from '../UnreadCountContext';

const Notifications = () => {
  const [sellingNotifications, setSellingNotifications] = useState([
    {
      id: 1,
      user: 'John Smith',
      message: 'Selling a desk lamp',
      date: '2024-09-15T10:30:00Z',
      isRead: false,
    },
    {
      id: 3,
      user: 'Michael Brown',
      message: 'Selling a bicycle',
      date: '2024-09-10T14:45:00Z',
      isRead: false,
    },
    {
      id: 5,
      user: 'David Lee',
      message: 'Selling a coffee maker',
      date: '2024-09-17T11:00:00Z',
      isRead: false,
    },
    {
      id: 7,
      user: 'Chris Miller',
      message: 'Selling a gaming monitor',
      date: '2024-09-14T13:10:00Z',
      isRead: true,
    },
    {
      id: 9,
      user: 'James Clark',
      message: 'Selling an air conditioner',
      date: '2024-09-11T12:25:00Z',
      isRead: true,
    },
  ]);

  const [buyingNotifications, setBuyingNotifications] = useState([
    {
      id: 2,
      user: 'Emily White',
      message: 'Looking for a mini fridge',
      date: '2024-09-13T09:15:00Z',
      isRead: true,
    },
    {
      id: 4,
      user: 'Sarah Johnson',
      message: 'Looking for a printer',
      date: '2024-09-12T08:20:00Z',
      isRead: true,
    },
    {
      id: 6,
      user: 'Sophia Davis',
      message: 'Looking for a study chair',
      date: '2024-09-16T16:35:00Z',
      isRead: true,
    },
    {
      id: 8,
      user: 'Olivia Wilson',
      message: 'Looking for a bookshelf',
      date: '2024-09-18T07:50:00Z',
      isRead: true,
    },
    {
      id: 10,
      user: 'Mia Thompson',
      message: 'Looking for a TV stand',
      date: '2024-09-09T15:40:00Z',
      isRead: true,
    },
  ]);

  const [filter, setFilter] = useState('Selling');
  const { setUnreadCount } = useUnreadCount();

  useEffect(() => {
    // Update unread count
    const count = [...sellingNotifications, ...buyingNotifications].filter(notification => !notification.isRead).length;
    setUnreadCount(count);
    sessionStorage.setItem('notificationCount', count.toString());
  }, [sellingNotifications, buyingNotifications, setUnreadCount]);

  const notificationsToDisplay = (filter === 'Selling' ? sellingNotifications : buyingNotifications)
    .slice()
    .sort((a, b) => new Date(b.date) - new Date(a.date));

  const [newNotification, setNewNotification] = useState({
    user: '',
    message: '',
    date: new Date().toISOString(),
    isRead: false,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewNotification({ ...newNotification, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const updatedBuyingNotifications = [
      ...buyingNotifications,
      { ...newNotification, id: buyingNotifications.length + sellingNotifications.length + 1 },
    ];
    setBuyingNotifications(updatedBuyingNotifications);
    setNewNotification({
      user: '',
      message: '',
      date: new Date().toISOString(),
      isRead: false,
    });
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Notifications</h1>
      <div style={styles.filterContainer}>
        <label htmlFor="filter">Filter by:</label>
        <select
          id="filter"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          style={styles.dropdown}
        >
          <option value="Selling">Selling</option>
          <option value="Buying">Buying</option>
        </select>
      </div>
      <div style={styles.notifications}>
        {notificationsToDisplay.map((notification) => (
          <div key={notification.id} style={styles.notification}>
            <div style={styles.header}>
              <div style={styles.user}>{notification.user}</div>
              <div style={styles.date}>
                {new Date(notification.date).toLocaleString()}
              </div>
            </div>
            <div style={!notification.isRead ? styles.boldMessage : styles.message}>
              {notification.message}
            </div>
            <button style={styles.contactButton}>Contact</button>
          </div>
        ))}
      </div>
      <div style={styles.formContainer}>
        <h2 style={styles.formTitle}>Post a New Request</h2>
        <form onSubmit={handleSubmit} style={styles.form}>
          <input
            type="text"
            name="user"
            value={newNotification.user}
            onChange={handleInputChange}
            placeholder="Your Name"
            required
            style={styles.input}
          />
          <textarea
            name="message"
            value={newNotification.message}
            onChange={handleInputChange}
            placeholder="What are you looking for?"
            required
            style={styles.textarea}
          />
          <button type="submit" style={styles.submitButton}>Post</button>
        </form>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '2rem',
    maxWidth: '100vw',
    backgroundColor: '#f5f8fa',
    fontFamily: 'Arial, sans-serif',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '1rem',
    textAlign: 'center',
    color: '#333',
  },
  filterContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: '1rem',
  },
  dropdown: {
    marginLeft: '0.5rem',
    padding: '0.5rem',
    fontSize: '1rem',
  },
  notifications: {
    display: 'grid',
    gap: '1rem',
  },
  notification: {
    padding: '1rem',
    backgroundColor: '#fff',
    border: '1px solid #e1e8ed',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '0.5rem',
  },
  user: {
    fontWeight: 'bold',
  },
  date: {
    color: '#657786',
  },
  unreadDot: {
    width: '10px',
    height: '10px',
    backgroundColor: '#1da1f2',
    borderRadius: '50%',
    marginLeft: '10px',
  },
  message: {
    marginBottom: '1rem',
    color: '#333',
  },
  boldMessage: {
    marginBottom: '1rem',
    color: '#333',
    fontWeight: 'bold',
  },
  contactButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#1da1f2',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1rem',
  },
  formContainer: {
    marginTop: '2rem',
    padding: '1rem',
    backgroundColor: '#fff',
    border: '1px solid #e1e8ed',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
  formTitle: {
    fontSize: '1.5rem',
    marginBottom: '1rem',
    textAlign: 'center',
    color: '#333',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem',
  },
  input: {
    padding: '0.5rem',
    fontSize: '1rem',
    borderRadius: '4px',
    border: '1px solid #e1e8ed',
  },
  textarea: {
    padding: '0.5rem',
    fontSize: '1rem',
    borderRadius: '4px',
    border: '1px solid #e1e8ed',
    resize: 'vertical',
  },
  submitButton: {
    padding: '0.5rem 1rem',
    backgroundColor: '#1da1f2',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1rem',
  },
};

export default Notifications;
