//Generate code for a notifications page that will show when user's post about an item they're looking for. Make it beautiful. It sshould look like a thread of messages with twitter like ui. Use dummy notifications for now.

const Notifications = () => {
  const [notifications, setNotifications] = useState([
    {
        id: 1,
        user: 'John Smith',
        message: 'Selling a desk lamp',
        date: '2024-09-15T10:30:00Z'
    },
    {
        id: 2,
        user: 'Emily White',
        message: 'Looking for a mini fridge',
        date: '2024-09-13T09:15:00Z'
    },
    {
        id: 3,
        user: 'Michael Brown',
        message: 'Selling a bicycle',
        date: '2024-09-10T14:45:00Z'
    },
    {
        id: 4,
        user: 'Sarah Johnson',
        message: 'Looking for a printer',
        date: '2024-09-12T08:20:00Z'
    },
    {
        id: 5,
        user: 'David Lee',
        message: 'Selling a coffee maker',
        date: '2024-09-17T11:00:00Z'
    },
    {
        id: 6,
        user: 'Sophia Davis',
        message: 'Looking for a study chair',
        date: '2024-09-16T16:35:00Z'
    },
    {
        id: 7,
        user: 'Chris Miller',
        message: 'Selling a gaming monitor',
        date: '2024-09-14T13:10:00Z'
    },
    {
        id: 8,
        user: 'Olivia Wilson',
        message: 'Looking for a bookshelf',
        date: '2024-09-18T07:50:00Z'
    },
    {
        id: 9,
        user: 'James Clark',
        message: 'Selling an air conditioner',
        date: '2024-09-11T12:25:00Z'
    },
    {
        id: 10,
        user: 'Mia Thompson',
        message: 'Looking for a TV stand',
        date:'2024-09-09T15:40:00Z'
    }])
    return (    
        <div style={{ padding: '2rem', maxWidth: '100vw'}}>
            <h1>Notifications</h1>
            <div style={{ display: 'grid', gap: '1rem' }}>
                {notifications.map((notification) => (
                    <div
                        key={notification.id}
                        style={{
                            padding: '1rem',
                            backgroundColor: '#fff',
                            border: '1px solid #B3A369',
                            borderRadius: '4px'
                        }}
                    >
                        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                            <div style={{ fontWeight: 'bold' }}>{notification.user}</div>
                            <div style={{ color: '#666' }}>{new Date(notification.date).toLocaleString()}</div>
                        </div>
                        <div>{notification.message}</div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Notifications;
