import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home';
import Bookmarks from './pages/Bookmarks';
import GlobalStyles from './styles/GlobalStyles';
import Layout from './components/Layout';
import Dashboard from './pages/Dashboard';
import SellItem from './pages/SellItem';
import Messages from './pages/Messages';
import ItemDetails from './pages/ItemDetails';
import Notifications from './pages/Notifications';

function App() {
  const [bookmarks, setBookmarks] = useState([]);
  const Notifications = () => {
    const [notifications, setNotifications] = useState([
      {
        id: 1,
        user: 'John Smith',
        message: 'Selling a desk lamp',
        date: '2024-09-15T10:30:00Z',
        isRead: false,
      },
      {
        id: 2,
        user: 'Emily White',
        message: 'Looking for a mini fridge',
        date: '2024-09-13T09:15:00Z',
        isRead: true,
      },
      {
        id: 3,
        user: 'Michael Brown',
        message: 'Selling a bicycle',
        date: '2024-09-10T14:45:00Z',
        isRead: false,
      },
      {
        id: 4,
        user: 'Sarah Johnson',
        message: 'Looking for a printer',
        date: '2024-09-12T08:20:00Z',
        isRead: true,
      },
      {
        id: 5,
        user: 'David Lee',
        message: 'Selling a coffee maker',
        date: '2024-09-17T11:00:00Z',
        isRead: false,
      },
      {
        id: 6,
        user: 'Sophia Davis',
        message: 'Looking for a study chair',
        date: '2024-09-16T16:35:00Z',
        isRead: true,
      },
      {
        id: 7,
        user: 'Chris Miller',
        message: 'Selling a gaming monitor',
        date: '2024-09-14T13:10:00Z',
        isRead: false,
      },
      {
        id: 8,
        user: 'Olivia Wilson',
        message: 'Looking for a bookshelf',
        date: '2024-09-18T07:50:00Z',
        isRead: true,
      },
      {
        id: 9,
        user: 'James Clark',
        message: 'Selling an air conditioner',
        date: '2024-09-11T12:25:00Z',
        isRead: false,
      },
      {
        id: 10,
        user: 'Mia Thompson',
        message: 'Looking for a TV stand',
        date: '2024-09-09T15:40:00Z',
        isRead: true,
      },
    ]);

  const toggleBookmark = (item) => {
    if (bookmarks.includes(item)) {
      setBookmarks(bookmarks.filter((bookmark) => bookmark !== item));
      return false;
    } else {
      setBookmarks([...bookmarks, item]);
      return true;
    }
  };

  return (
    <Router>
      <GlobalStyles />
      <Layout>
        <Routes>
          <Route
            path="/"
            element={<Home bookmarks={bookmarks} toggleBookmark={toggleBookmark} />}
          />
          <Route
            path="/bookmarks"
            element={<Bookmarks bookmarks={bookmarks} toggleBookmark={toggleBookmark} />}
          />
          <Route path="/item" element={<Dashboard />} />
          <Route path="/item-details/:id" element={<ItemDetails />} />
          <Route path="/sell-item" element={<SellItem />} />
          <Route path="/notifications" element={<Notifications />} />
          <Route path="/messages" element={<Messages />} />
        </Routes>
      </Layout>
    </Router>
  );
}
}
export default App;
