import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';

const ItemDetails = () => {
  const { id } = useParams();
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  // Retrieve items from sessionStorage
  const storedItems = sessionStorage.getItem('items');
  const items = storedItems ? JSON.parse(storedItems) : [];

  // Find the relevant item
  const item = items.find(item => item._id === id);

  const handleNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex + 1) % item.list_of_images.length);
  };

  const handlePrevImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex - 1 + item.list_of_images.length) % item.list_of_images.length);
  };

  if (!item) {
    return <div>Item not found</div>;
  }

  return (
    <div style={styles.overlay}>
      <div style={styles.container}>
        <div style={styles.itemDetails}>
          <h2 style={styles.title}>{item.name}</h2>
          <img src={item.list_of_images[currentImageIndex]} alt={item.name} style={styles.image} />
          {item.list_of_images.length > 1 && (
            <div style={styles.imageNavigation}>
              <button onClick={handlePrevImage} style={styles.navButton}>
                &lt;
              </button>
              <button onClick={handleNextImage} style={styles.navButton}>
                &gt;
              </button>
            </div>
          )}
          <p style={styles.text}>Category: {item.category}</p>
          <p style={styles.text}>Price: {item.price}</p>
          <p style={styles.text}>Seller: {item.vendor}</p>
          <Link to={`/messages?to=${item.vendor}`} style={styles.contactButton}>
            Contact Seller
          </Link>
        </div>
      </div>
    </div>
  );
};

const styles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    minHeight: '100vh',
    padding: '2rem',
  },
  itemDetails: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    maxWidth: '600px',
    width: '100%',
    textAlign: 'center',
  },
  title: {
    fontSize: '2rem',
    marginBottom: '1rem',
  },
  image: {
    width: '100%',
    maxWidth: '400px',
    borderRadius: '8px',
    marginBottom: '1rem',
  },
  imageNavigation: {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '1rem',
  },
  navButton: {
    background: 'transparent',
    border: 'none',
    cursor: 'pointer',
    fontSize: '1.5rem',
  },
  text: {
    fontSize: '1rem',
    marginBottom: '0.5rem',
  },
  contactButton: {
    display: 'inline-block',
    padding: '0.75rem 1.5rem',
    backgroundColor: '#007bff',
    color: '#fff',
    borderRadius: '4px',
    textDecoration: 'none',
    fontSize: '1rem',
    marginTop: '1rem',
  },
};

export default ItemDetails;
