import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ItemComponent from '../components/ItemComponent'; // Adjust the import path as necessary
import { CATEGORIES } from '../path/to/your/categoriesFile'; // Import your CATEGORIES

const categories = ['All', 'Clothing', 'Shoes', 'Accessories', 'Electronics', 'Room'];

const Home = ({ bookmarks, toggleBookmark }) => {
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [searchInput, setSearchInput] = useState('');

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/v1/items/');
        setItems(response.data);
        setFilteredItems(response.data);
      } catch (error) {
        console.error('Error fetching items:', error);
      }
    };
    fetchItems();
  }, []);

  const handleSearch = () => {
    const pattern = new RegExp(searchInput, 'gi');
    let filtered = items.filter(item => pattern.test(item.name));

    // If no name match, search categories
    if (filtered.length === 0) {
      for (const [category, keywords] of Object.entries(CATEGORIES)) {
        if (keywords.some(keyword => pattern.test(keyword))) {
          filtered = items.filter(item => item.category.includes(category));
          break;
        }
      }
    }

    setFilteredItems(filtered.length > 0 ? filtered : []);
  };

  return (
    <div style={{ padding: '2rem', maxWidth: '100vw' }}>
      <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
        {categories.map((category) => (
          <button
            key={category}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: selectedCategory === category ? '#B3A369' : '#fff',
              color: selectedCategory === category ? '#fff' : '#B3A369',
              border: '1px solid #B3A369',
              cursor: 'pointer',
              borderRadius: '4px',
            }}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>

      {filteredItems.length === 0 ? (
        <p>No item found fits the search criteria</p>
      ) : (
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '1rem' }}>
          {filteredItems.map((item) => (
            <ItemComponent
              key={item._id}
              item={item}
              bookmarks={bookmarks}
              toggleBookmark={toggleBookmark}
              style={{ height: '310px', width: '320px', overflow: 'hidden' }} // Set consistent height
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
