// src/pages/ItemDetails.js

import React from 'react';
import { useParams, Link } from 'react-router-dom';

const ItemDetails = () => {
  const { _id } = useParams();

 // Retrieve items from sessionStorage
 const storedItems = sessionStorage.getItem('items');
 const items = storedItems ? JSON.parse(storedItems) : [];

 // Find the relevant item
 const item = items.find(item => item._id === _id);

 if (!item) {
   return <div>Item not found</div>;
 }

  return (
    <div style={{ padding: '1rem' }}>
      <h2>{item.name}</h2>
      <img src={item.image} alt={item.title} style={{ width: '100%', maxWidth: '400px' }} />
      <p>Category: {item.category}</p>
      <p>Price: {item.price}</p>
      <p>Seller: {item.vendor}</p>
      <Link to={`/messages?to=${item.vendor}`}>Message Seller</Link>
    </div>
  );
};

export default ItemDetails;
