import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import { useUnreadCount } from '../UnreadCountContext';

const Navbar = styled.nav`
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: black; /* Vanderbilt dark blue */
  padding: 1rem 2rem;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 1.5rem;
`;

const StyledNavLink = styled(NavLink)`
  color: #FFFFFF;
  text-decoration: none;
  font-weight: bold;

  &.active {
   text-decoration: underline;
  }

  &:hover {
    color: #ECEFF1;
  }
`;

const NotificationBadge = styled.span`
  position: absolute;
  top: -14px;
  right: -10px;
  background-color: red;
  color: white;
  border-radius: 50%;
  padding: 4px 10px;
  font-size: 12px;
`;

const SearchBar = styled.input`
  padding: 0.5rem;
  border-radius: 4px;
  border: 1px solid #ccc;
  margin-right: 1rem;
`;

const Layout = ({ children, items, setItems }) => {
  const { unreadCount } = useUnreadCount();
  const [searchInput, setSearchInput] = useState('');

  const CATEGORIES = {
    "Furniture": ["couch", "sofa", "chair", "table", "bed", "dresser", "bookshelf", "stool", "cabinet", "armchair", "recliner", "nightstand", "ottoman", "bench", "coffee table", "bar stool"],
    "Electronics": ["phone","scooter", "vision pro", "ipad", "watch", "laptop", "television", "tv", "computer", "camera", "tablet", "smartwatch", "headphones", "speaker", "printer", "monitor", "console", "gaming", "drone", "router", "bluetooth speaker", "VR headset"],
    "Clothing": ["shirt", "pants", "jacket", "dress", "shoes", "jeans", "t-shirt", "shorts", "coat", "sweater", "skirt", "blouse", "hoodie", "boots", "suit", "tie", "blazer", "sweatshirt", "pajamas", "underwear", "socks", "Nike", "Adidas", "Puma", "Under Armour", "Levi's", "Gucci", "Zara", "H&M", "Uniqlo", "Gap", "Ralph Lauren", "Tommy Hilfiger", "Calvin Klein", "Lacoste", "Louis Vuitton", "Prada", "Balenciaga", "Versace", "Off-White", "Supreme", "Fila", "North Face"],
    "Accessories": ["hat", "watch", "earrings", "necklace", "bracelet", "belt", "sunglasses", "scarf", "gloves", "tie", "wallet", "bag", "backpack", "bottle", "ring", "hair clips", "pins", "keychain", "umbrella"],
    "Books": ["book", "magazine", "novel", "comic", "textbook", "journal", "manual", "dictionary", "encyclopedia", "e-book", "graphic novel", "children's book", "cookbook", "poetry"],
    "Room": ["desk", "lamp", "rug", "curtain", "mirror", "vase", "wall art", "plant", "picture frame", "blanket", "pillow", "shelves", "night light", "alarm clock", "fan", "air purifier"],
    "Kitchen": ["microwave", "toaster", "blender", "fridge", "coffee maker", "oven", "kettle", "mixer", "cutlery", "plates", "mugs", "pans", "pots", "grill", "dish rack", "cutting board", "food processor", "slow cooker", "rice cooker", "water filter"],
    "Toys & Games": ["action figure", "board game", "puzzle", "toy car", "lego", "doll", "game console", "video game", "teddy bear", "stuffed animal", "remote control car", "model kit", "card game", "building blocks"],
    "Sports Equipment": ["soccer ball", "dumbbells", "basketball", "tennis racket", "golf club", "helmet", "bicycle", "ball", "weights", "yoga mat", "running shoes", "baseball bat", "hockey stick", "football", "exercise bike", "jump rope", "treadmill"],
    "Music Instruments": ["guitar", "keyboard", "piano", "drum", "violin", "trumpet", "saxophone", "flute", "microphone", "amp", "clarinet", "accordion", "ukulele", "electric guitar", "trombone", "harp"],
    "Home Appliances": ["vacuum cleaner", "air conditioner", "heater", "fan", "washing machine", "dryer", "dishwasher", "refrigerator", "stove", "freezer", "dehumidifier", "humidifier", "water heater", "electric kettle", "microwave oven", "rice cooker"],
    "Outdoor": ["tent", "camping chair", "cooler", "grill", "hiking boots", "backpack", "lantern", "bicycle", "skateboard", "roller skates", "patio furniture", "hammock", "fishing rod", "kayak", "outdoor umbrella"],
    "Pet Supplies": ["dog bed", "cat tree", "leash", "collar", "pet food", "litter box", "fish tank", "bird cage", "pet carrier", "pet toys", "pet grooming tools", "dog crate", "aquarium filter"],
    "Automotive": ["car", "motorcycle", "helmet", "tire", "car seat", "car battery", "oil", "windshield wipers", "car cover", "bike rack", "jump starter", "car charger", "dashboard camera", "seat covers"],
    "Health & Beauty": ["shampoo", "conditioner", "perfume", "lotion", "makeup", "nail polish", "razor", "hair dryer", "toothbrush", "massage chair", "sunscreen", "face mask", "hand sanitizer", "body wash", "lip balm", "electric toothbrush", "deodorant"],
    "Office Supplies": ["printer", "paper", "stapler", "pen", "notebook", "highlighter", "calculator", "paper clips", "desk organizer", "whiteboard", "file folders", "envelopes", "scissors", "sticky notes", "printer ink"],
    "Gardening": ["shovel", "watering can", "garden hose", "plant seeds", "flower pot", "lawn mower", "fertilizer", "rake", "trowel", "wheelbarrow", "pruning shears", "compost bin", "garden gloves", "hedge trimmer"],
    "Baby Products": ["stroller", "crib", "baby bottle", "diaper", "pacifier", "baby monitor", "high chair", "baby blanket", "baby wipes", "car seat", "baby walker", "playpen", "baby carrier", "changing table"],
    "Fitness": ["yoga mat", "dumbbells", "exercise bike", "treadmill", "resistance bands", "pull-up bar", "jump rope", "kettlebell", "rowing machine", "weight bench", "medicine ball"],
    "Travel": ["luggage", "backpack", "travel pillow", "passport holder", "travel adapter", "packing cubes", "suitcase", "duffle bag", "carry-on bag"],
    "Shoes": ["sneakers", "boots", "sandals", "heels", "flats", "loafers", "running shoes", "dress shoes", "flip flops", "slippers", "wedges", "oxfords", "ballet flats", "espadrilles", "clogs", "ankle boots", "platforms", "Nike", "Adidas", "Puma", "Reebok", "Converse", "Vans", "Timberlands", "Dr. Martens", "Clarks", "New Balance", "Under Armour", "Skechers", "Crocs", "Birkenstock", "UGG", "Asics", "Jordan", "Gucci", "Louis Vuitton", "Prada", "Balenciaga"],
    "Crafts & Hobbies": ["sewing machine", "fabric", "paintbrushes", "paint", "canvas", "yarn", "knitting needles", "scissors", "glue", "beads", "scrapbooking paper", "crochet hooks"],
  };

  const handleSearch = (event) => {
    event.preventDefault();
    const searchTerm = searchInput.toLowerCase();

    // Filter the items directly in the current page
    const matchedItems = items.filter(item => {
      const itemNameMatch = item.name.toLowerCase().includes(searchTerm);
      const categoryMatch = Object.entries(CATEGORIES).some(([category, keywords]) =>
        keywords.includes(searchTerm) && item.category === category
      );
      return itemNameMatch || categoryMatch;
    });

    setItems(matchedItems); // Directly update items on the homepage
  };

  return (
    <>
      <Navbar>
        <NavLink to="/" style={{ color: '#FFFFFF', textDecoration: 'none', fontWeight: 'bold' }}>
          Reuse Vandy
        </NavLink>
        <form onSubmit={handleSearch} style={{ display: 'flex', alignItems: 'center' }}>
          <SearchBar
            type="text"
            placeholder="Search..."
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <button type="submit" style={{ padding: '0.5rem', borderRadius: '4px', border: 'none', cursor: 'pointer' }}>
            Search
          </button>
        </form>
        <NavLinks>
          <StyledNavLink to="/" exact>Home</StyledNavLink>
          <StyledNavLink to="/notifications" style={{ position: 'relative' }}>
            Notifications
            {unreadCount > 0 && (
              <NotificationBadge>{unreadCount}</NotificationBadge>
            )}
          </StyledNavLink>
          <StyledNavLink to="/messages">Messages</StyledNavLink>
          <StyledNavLink to="/sell-item">Sell Your Item</StyledNavLink>
          <StyledNavLink to="/bookmarks">View Bookmarks</StyledNavLink>
        </NavLinks>
      </Navbar>
      <main>{children}</main>
    </>
  );
};

export default Layout;
